import React, { useEffect, useState } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import { socket } from "./socket";

const AIStory = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const { room } = location.state || {};
  const [aiStory, setAiStory] = useState("Loading AI response...");
  const [winner, setWinner] = useState("Unknown");

  useEffect(() => {
    socket.on("show_ai_story", ({ winner, aiStory }) => {
      console.log("ðŸŽ‰ Received show_ai_story event!");
      console.log(`ðŸ† Winner Received: ${winner}`);
      console.log(`ðŸ“œ AI Story Received: ${aiStory}`);

      setWinner(winner);
      setAiStory(aiStory);
    });

    socket.on("go_to_results", (data) => {
      navigate("/results", { state: { winner: data.winner } });
    });

    return () => {
      socket.off("show_ai_story");
      socket.off("go_to_results");
    };
  }, [navigate]);

  return (
    <div>
      <h2>AI Generated Story</h2>
      <p>{aiStory}</p>
      <h3>Winning Player: {winner}</h3>
    </div>
  );
};

export default AIStory;
