import React, { useState, useEffect } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import { socket } from "./socket";

const Voting = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const { room, prompts } = location.state;
  const [selectedPrompt, setSelectedPrompt] = useState(null);
  const [voteSubmitted, setVoteSubmitted] = useState(false);

  useEffect(() => {
    socket.on("game_over", (winnerData) => {
      navigate("/results", { state: { winner: winnerData.winner } });
    });

    return () => socket.off("game_over");
  }, [navigate]);

  const submitVote = () => {
    if (selectedPrompt !== null && !voteSubmitted) {
      socket.emit("submit_vote", { room, votedPrompt: selectedPrompt });
      setVoteSubmitted(true); // Prevent further voting
    }
  };

  return (
    <div>
      <h2>Vote for the Best Submission</h2>
      {prompts.map((entry, index) => (
        <div key={index}>
          <button
            onClick={() => setSelectedPrompt(entry.prompt)}
            style={{ background: selectedPrompt === entry.prompt ? "lightblue" : "white" }}
            disabled={voteSubmitted} // Disable buttons after voting
          >
            {entry.prompt}
          </button>
        </div>
      ))}
      <button onClick={submitVote} disabled={selectedPrompt === null || voteSubmitted}>
        Submit Vote
      </button>
    </div>
  );
};

export default Voting;
