import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import io from "socket.io-client";

const socket = io.connect("http://localhost:3001");

function EnterRoom() {
  const [room, setRoom] = useState("");
  const [errorMessage, setErrorMessage] = useState("");
  const navigate = useNavigate();

  const handleCreateRoom = () => {
    socket.emit("create_room", (newRoom) => {
      navigate(`/lobby/${newRoom}`); // Redirect to the new room
    });
  };

  const handleJoinRoom = () => {
    if (room.trim() !== "") {
      const userName = localStorage.getItem("userName");

      socket.emit("join_room", { name: userName, room }, (response) => {
        if (response.success) {
          navigate(`/lobby/${room}`); // Redirect to lobby if room is valid
        } else {
          setErrorMessage(response.message); // Show error if room is invalid
        }
      });
    }
  };

  return (
    <div>
      <h2>Join or Create a Game</h2>

      <button onClick={handleCreateRoom}>Create Game</button>

      <h3>Or Join a Game</h3>
      <input
        type="text"
        placeholder="Enter game code"
        value={room}
        onChange={(e) => setRoom(e.target.value)}
      />
      <button onClick={handleJoinRoom}>Join Room</button>

      {errorMessage && <p style={{ color: "red" }}>{errorMessage}</p>}
    </div>
  );
}

export default EnterRoom;
