import React, { useEffect, useState } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import { socket } from "../socket";

const Lobby = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const { name, room, isCreator } = location.state;
  const [players, setPlayers] = useState([]);

  useEffect(() => {
    socket.emit("join_room", { name, room }, (response) => {
      if (!response.success) {
        navigate("/");
      }
    });

    socket.on("update_users", (users) => {
      setPlayers(users);
    });

    socket.on("game_started", () => {
      navigate("/game", { state: { name, room } });
    });

    return () => socket.off("update_users");
  }, [navigate, name, room]);

  const startGame = () => {
    socket.emit("start_game", room);
  };

  return (
    <div>
      <h2>Lobby - Game Code: {room}</h2>
      <h3>Players:</h3>
      <ul>
        {players.map((player, index) => (
          <li key={index}>{player.name}</li>
        ))}
      </ul>
      {isCreator && <button onClick={startGame}>Start Game</button>}
    </div>
  );
};

export default Lobby;
