import React, { useState, useEffect } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import { socket } from "./socket";

const Voting = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const { name, room } = location.state;
  const [prompts, setPrompts] = useState({});
  const [selectedPrompt, setSelectedPrompt] = useState(null);

  useEffect(() => {
    socket.on("prompts_ready", (data) => {
      setPrompts(data);
    });

    socket.on("game_over", (winnerData) => {
      navigate("/results", { state: { winner: winnerData.winner } });
    });

    return () => socket.off("prompts_ready");
  }, [navigate]);

  const submitVote = () => {
    if (selectedPrompt) {
      socket.emit("submit_vote", { room, votedFor: selectedPrompt });
    }
  };

  return (
    <div>
      <h2>Vote for the Best Prompt</h2>
      {Object.entries(prompts).map(([playerId, prompt]) => (
        <div key={playerId}>
          <button
            onClick={() => setSelectedPrompt(playerId)}
            style={{ background: selectedPrompt === playerId ? "lightblue" : "white" }}
          >
            {prompt}
          </button>
        </div>
      ))}
      <button onClick={submitVote} disabled={!selectedPrompt}>Submit Vote</button>
    </div>
  );
};

export default Voting;
