import React, { useEffect } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import { socket } from "./socket";

const AIStory = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const { room, aiStory, winner, isCreator } = location.state;

  useEffect(() => {
    socket.on("go_to_results", (data) => {
      navigate("/results", { state: { winner: data.winner } });
    });

    return () => socket.off("go_to_results");
  }, [navigate]);

  const handleNext = () => {
    socket.emit("next_to_results", room);
  };

  return (
    <div>
      <h2>AI Generated Story</h2>
      <p>{aiStory}</p>
      <h3>Winning Player: {winner}</h3>
      {isCreator && <button onClick={handleNext}>Next</button>}
    </div>
  );
};

export default AIStory;
