import React, { useEffect, useState } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import { socket } from "./socket";

const AIStory = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const { room } = location.state || {};
  
  const [aiStory, setAiStory] = useState("Loading AI response...");
  const [winner, setWinner] = useState("Unknown");
  const [eventReceived, setEventReceived] = useState(false); // Track if event is received

  useEffect(() => {
    console.log("üü° AIStory.js mounted. Waiting for show_ai_story event...");

    socket.on("show_ai_story", ({ winner, aiStory }) => {
      console.log("üéâ Received show_ai_story event!");
      console.log(`üèÜ Winner Received: ${winner}`);
      console.log(`üìú AI Story Received: ${aiStory}`);

      setWinner(winner);
      setAiStory(aiStory || "No AI response received.");
      setEventReceived(true); // Mark event as received
    });

    return () => {
      console.log("üî¥ Cleaning up event listener for show_ai_story.");
      socket.off("show_ai_story");
    };
  }, []);

  return (
    <div>
      <h2>AI Generated Story</h2>
      <p>{eventReceived ? aiStory : "‚ö†Ô∏è No AI response received. Check server connection."}</p>
      <h3>Winning Player: {winner}</h3>
    </div>
  );
};

export default AIStory;
